try {
  /****************************************************************
   * 1.  Helper: recursively flatten an object
   ****************************************************************/
  function flattenObject(obj, prefix = '') {
    const out = {};
    for (const k in obj) {
      if (!Object.prototype.hasOwnProperty.call(obj, k)) continue;

      const v       = obj[k];
      const newKey  = prefix ? `${prefix}.${k}` : k;

      // ‚Äì plain object ‚Üí recurse
      if (v && typeof v === 'object' && !Array.isArray(v)) {
        Object.assign(out, flattenObject(v, newKey));

      // ‚Äì array  ‚Üí recurse over indices
      } else if (Array.isArray(v)) {
        v.forEach((entry, i) => {
          if (entry && typeof entry === 'object') {
            Object.assign(out, flattenObject(entry, `${newKey}.${i}`));
          } else {
            out[`${newKey}.${i}`] = entry;
          }
        });

      // ‚Äì primitive ‚Üí keep
      } else {
        out[newKey] = v;
      }
    }
    return out;
  }

  /****************************************************************
   * 2.  Crunch every incoming bundle
   ****************************************************************/
  const bundles = $input.all();   // every inbound item
  const rows    = [];

  bundles.forEach(bundle => {
    const dataArr = bundle.json?.data;

    // If the bundle has a json.data array ‚Üí emit one row per element
    if (Array.isArray(dataArr)) {
      dataArr.forEach(entry => rows.push(flattenObject(entry)));

    // Otherwise just flatten the bundle‚Äôs json itself
    } else if (bundle.json) {
      rows.push(flattenObject(bundle.json));
    }
  });

  /****************************************************************
   * 3.  Return rows (Make/n8n will output one item per object)
   ****************************************************************/
  return rows;

} catch (err) {
  throw new Error('Flattening failed: ' + err.message);
}

try {
	/***********************
	 * 0.  Grab ALL incoming items at once
	 **********************/
	const allItems   = $input.all();           // ‚Üê every inbound bundle
	const aggregated = [];                     // ‚Üê we‚Äôll push results here

	/***********************
	 * 1.  Process every item individually
	 **********************/
	for (const item of allItems) {

		/* 1.1 Pull raw text */
		const rawOutput = item.json.output || "";

		/* 1.2 Strip ‚Å†‚ÄØ fences (json / html / any-tag) */
		const jsonMatch = rawOutput.match(/```(?:json)?\s*([\s\S]*?)\s*```/)
		let   cleanedOutput  = jsonMatch ? jsonMatch[1] : rawOutput;

		/* 1.3 Remove big HTML wrappers */
		cleanedOutput = cleanedOutput.replace(
			/<!DOCTYPE[^>]>|<html[^>]>|<\/html>|<head[^>]>[\s\S]?<\/head>|<body[^>]*>|<\/body>/gi,
			''
		);

		/* 1.4 Nuke BOM + trim */
		cleanedOutput = cleanedOutput.replace(/^\uFEFF/, '').trim();

		/* 1.5 Keep \n \r \t, sanitize rest of the control chars */
		cleanedOutput = cleanedOutput.replace(/[\u0000-\u001F\u007F]/g, c =>
			(c === '\n' || c === '\r' || c === '\t') ? c : ' '
		);

		/* 1.6 Flatten HTML in JSON fields */
		cleanedOutput = cleanedOutput
			.replace(/>\s+</g, '><')   // remove spaces between tags
			.replace(/\s+/g, ' ')      // collapse multiple spaces
			.trim();

		/* 1.7 Parse JSON, 2-step fallback */
		let parsedData;
		try {
			parsedData = JSON.parse(cleanedOutput);
		} catch (e) {
			const esc = cleanedOutput.replace(/>([^<]+)</g, (_, p1) => '>' + p1.replace(/"/g, '\\"') + '<');
			parsedData = JSON.parse(esc);
		}

		/* 1.8 Recursively turn any HTML strings into single-line text */
		function stripHtmlLines(d) {
			if (typeof d === 'string')
				return d.replace(/[\n\r]+/g, ' ').replace(/\s+/g, ' ').trim();
			if (Array.isArray(d))
				return d.map(stripHtmlLines);
			if (d && typeof d === 'object') {
				const obj = {};
				for (const k in d) obj[k] = stripHtmlLines(d[k]);
				return obj;
			}
			return d;
		}
		parsedData = stripHtmlLines(parsedData);

		/* 1.9 Ensure we always push objects into the output array */
		if (Array.isArray(parsedData)) {
			aggregated.push(...parsedData.map(o => ({ ...o })));
		} else if (parsedData && typeof parsedData === 'object') {
			aggregated.push({ ...parsedData });
		} else {
			throw new Error('Unsupported JSON format ‚Äì expected array or object.');
		}
	}

	/***********************
	 * 2.  Return everything we collected
	 **********************/
	return aggregated;          // ‚Üê Make/n8n will emit one item per element

} catch (err) {
	throw new Error('Failed to parse JSON output: ' + err.message);
}
